import React, {useEffect, useState} from 'react'
import { listCredentials, revokeCredential } from '../api'
export default function RevocationPanel(){
    const [creds, setCreds] = useState([])
    useEffect(()=>{ listCredentials().then(setCreds) },[])
    async function revoke(id){
        const r = await revokeCredential(id)
        if(r.status==='revoked') { alert('revoked'); setCreds(creds.map(c=>
            c.id===id?{...c,revoked:true}:c)) }
        else alert('error')
    }
    return (
    <div className="card p-3">
        <h5>Revocation Panel</h5>
        <ul>
            {creds.map(c=> (
                <li key={c.id}>{c.id} - {c.revoked? 'REVOKED': 'VALID'} <button className="btn btn-sm btn-danger ms-2" onClick={()=>revoke(c.id)}>Revoke</button></li>
            ))}
            </ul>
        </div>
    )
}
