import React, { useState } from 'react'
import { createSchema } from '../api'
import { Form, Button, InputGroup } from 'react-bootstrap'

export default function SchemaCreator(){
  const [name, setName] = useState('')
  const [fields, setFields] = useState('name,email')

  async function onCreate(e){
    e?.preventDefault()
    const f = fields.split(',').map(s=>s.trim()).filter(Boolean)
    const r = await createSchema(name, f)
    if (r.schema_id) {
      alert('Schema created ✓')
      setName(''); setFields('')
    } else {
      alert('Error creating schema')
    }
  }

  return (
    <Form onSubmit={onCreate}>
      <Form.Group className="mb-2">
        <Form.Label>Schema name</Form.Label>
        <Form.Control placeholder="StudentID" value={name} onChange={e=>setName(e.target.value)} required />
      </Form.Group>

      <Form.Group className="mb-3">
        <Form.Label>Fields (comma separated)</Form.Label>
        <InputGroup>
          <Form.Control value={fields} onChange={e=>setFields(e.target.value)} />
          <Button variant="outline-secondary" onClick={onCreate}>Create</Button>
        </InputGroup>
        <Form.Text className="text-muted">Example: name,email,id</Form.Text>
      </Form.Group>
    </Form>
  )
}
