// frontend/src/components/Login.js
import React, { useState } from "react";
import "./login.css"; // make sure this file exists (below)
import { login } from "../api";

export default function Login({ onLogin }) {
  const [username, setUsername] = useState("issuer");
  const [password, setPassword] = useState("password");
  const [busy, setBusy] = useState(false);

  async function handleSubmit(e) {
    e?.preventDefault();
    setBusy(true);
    try {
      const res = await login(username, password);
      if (res && res.token) {
        onLogin(res.token);
      } else {
        alert("Login failed — check username/password");
      }
    } catch (err) {
      console.error(err);
      alert("Login error");
    } finally {
      setBusy(false);
    }
  }

  return (
    <div className="vaultx-login-root">
      <div className="vaultx-left-panel">
        <div className="vaultx-left-inner">
          {/* Inline logo / symbol - replace with your own if you have a file */}
          <svg className="vaultx-logo" viewBox="0 0 64 64" width="72" height="72" xmlns="http://www.w3.org/2000/svg" aria-hidden>
            <circle cx="32" cy="32" r="30" fill="rgba(255,255,255,0.06)"/>
            <path d="M32 8 L36 28 L56 32 L36 36 L32 56 L28 36 L8 32 L28 28 Z" fill="#fff" opacity="0.95"/>
          </svg>

          <h1 className="vaultx-title">Hello VaultX</h1>
          <p className="vaultx-sub">
            Securely manage credentials and identities from a single vault.
          </p>
        </div>
      </div>

      <div className="vaultx-right-panel">
        <div className="vaultx-form-box">
          <h2 className="form-title">Welcome to VAULTX</h2>

          <form onSubmit={handleSubmit} className="vaultx-form">
            <label className="form-label">Email</label>
            <input
              className="form-control"
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="you@domain.com"
              required
            />

            <label className="form-label">Password</label>
            <input
              className="form-control"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter password"
              required
            />

            <button type="submit" className="btn vaultx-btn" disabled={busy}>
              {busy ? "Signing in..." : "Login Now"}
            </button>
          </form>

          <div className="small-muted">
            <a href="#" onClick={(e) => e.preventDefault()}>Forgot password?</a>
          </div>
        </div>
      </div>
    </div>
  );
}
