import React, { useState } from "react";
import "./login.css";
import { login } from "../api"; // your existing api login helper (unchanged)

export default function Login({ onLogin }) {
  const [username, setUsername] = useState("issuer");
  const [password, setPassword] = useState("password");
  const [busy, setBusy] = useState(false);

  async function handleSubmit(e) {
    e?.preventDefault();
    setBusy(true);
    try {
      const res = await login(username, password);
      if (res && res.token) {
        onLogin(res.token);
      } else {
        alert("Login failed — check username/password");
      }
    } catch (err) {
      console.error(err);
      alert("Login error");
    } finally {
      setBusy(false);
    }
  }

  return (
    <div className="vx-login-page">
      <header className="vx-topbar">
        <div className="vx-topbar-inner">
          <div className="vx-logo-text">vault<span className="vx-x">X</span></div>
          <nav className="vx-nav">
            <a className="active">Home</a>
            <a>About</a>
          </nav>
        </div>
      </header>

      <main className="vx-main">
        <section className="vx-hero">
          <div className="vx-hero-card">
            <div className="vx-hero-left">
              <h1>Secure Your Personal<br/>Data & Documents</h1>

              <p className="vx-hero-desc">
                Advanced Protection for Your Digital Life
                <span className="vx-dot"> · </span>
                End-to-End Encryption
                <span className="vx-dot"> · </span>
                Multi-Factor Authentication
              </p>

              <div className="vx-icons-row" aria-hidden>
                {/* small illustrative icons - simple SVGs */}
                <div className="vx-icon-card">
                  <svg width="48" height="48" viewBox="0 0 24 24"><path fill="#2b6ef6" d="M3 3h8v8H3z"/><path fill="#bfe1ff" d="M13 3h8v8h-8z"/></svg>
                </div>
                <div className="vx-icon-card">
                  <svg width="48" height="48" viewBox="0 0 24 24"><path fill="#2b6ef6" d="M6 3h12v18H6z"/></svg>
                </div>
                <div className="vx-icon-card">
                  <svg width="48" height="48" viewBox="0 0 24 24"><path fill="#2b6ef6" d="M12 2a10 10 0 100 20 10 10 0 000-20z"/></svg>
                </div>
              </div>
            </div>

            <aside className="vx-hero-right">
              <div className="vx-login-card">
                <div className="vx-lock-header">
                  <svg className="vx-lock" viewBox="0 0 24 24" width="28" height="28">
                    <path fill="#1e61d6" d="M12 2a4 4 0 00-4 4v3H6a2 2 0 00-2 2v7a2 2 0 002 2h12a2 2 0 002-2v-7a2 2 0 00-2-2h-2V6a4 4 0 00-4-4z"/>
                  </svg>
                  <h3>Secure Login</h3>
                </div>

                <form className="vx-login-form" onSubmit={handleSubmit}>
                  <label className="vx-label">Username</label>
                  <input
                    className="vx-input"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    placeholder="Username"
                    required
                  />

                  <label className="vx-label">Password</label>
                  <input
                    className="vx-input"
                    type="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Password"
                    required
                  />

                  <button className="vx-btn" type="submit" disabled={busy}>
                    {busy ? "Signing in..." : "LOGIN"}
                  </button>

                  <div className="vx-forgot">
                    <a href="#" onClick={(e) => e.preventDefault()}>Forgot Username/Password?</a>
                  </div>
                </form>
              </div>
            </aside>
          </div>
        </section>
      </main>
    </div>
  );
}
