import React, {useState} from 'react'
import { login } from '../api'
export default function Login({onLogin}){
    const [u,setU]=useState('issuer'); const [p,setP]=useState('password');
    async function submit(e){
        e.preventDefault();
        const r = await login(u,p);
        if(r.token) onLogin(r.token);
        else alert('login failed')
    }
    return (
    <div className="container p-4">
        <h3>Issuer Login</h3>
        <form onSubmit={submit}>
        <input className="form-control my-2" value={u}onChange={e=>setU(e.target.value)} />
        <input type="password" className="form-control my-2" value={p}onChange={e=>setP(e.target.value)} />
        <button className="btn btn-primary">Login</button>
        </form>
        </div>
    )
}
